import { Component } from '@angular/core';

@Component({
  selector: 'app-two-way-data-bi',
  templateUrl: './two-way-data-bi.component.html',
  styleUrls: ['./two-way-data-bi.component.css']
})
export class TwoWayDataBiComponent {
  nome = "";

}
<h2>Two-way Data Binding</h2>
<label for="nome">Digite o nome:</label>
<input type="text" [(ngModel)]= "nome">
<p>Nome digitado: {{nome}}</p>
